import React from 'react';
import './drawerStories.css';
import { BrowserRouter, Route, Link,Switch } from 'react-router-dom';

export const DrawerStories = () =>{

	var canon = document.querySelector('li#canon_bottom');
	var legend = document.querySelector('li#legend_bottom');
	var whatBorder = true;

	function borderLegend(){

		if(whatBorder == true){
			legend.style.borderBottom = "10px solid green";
			canon.style.borderBottom ="none";
			whatBorder = false;
		}

	}
	function borderCanon(){

		if(whatBorder == false){
			canon.style.borderBottom = "10px solid green";
			legend.style.borderBottom ="none";
			whatBorder = true;
		}

	}

	return(
		<div className="drawerStories">
			<ul className="drawer-stories-list">
				<li className="drawer-stories-list-li" id="canon_bottom" onclick={borderCanon()}>
					<Link  to='/canon'>Canon</Link>
				</li>
				<li className="drawer-stories-list-li" id="legend_bottom" onclick={borderLegend()}>
					<Link  to='/legends'>Legends</Link>
				</li>
			</ul>
		</div>
);
}
