import React, { useState }from 'react';
import './stories.css';
import {Canon} from './canon/Canon'
import {Legends} from './legends/Legends'
import {DrawerStories} from './drawerStories/DrawerStories'
import { BrowserRouter, Route, Link,Switch } from 'react-router-dom';


export const Stories = () =>{

	const [bottomBorderCanon, setBottomBorderCanon] = useState();
	const [bottomBorderLegends, setBottomBorderLegends] = useState();


	function borderChanger(canon, legends){
		if(canon){
			const _legend = document.querySelector('li#legend_bottom');
			const _canon = document.querySelector('li#canon_bottom');

			_legend.style.borderBottom ="3px solid green";
			_canon.style.borderBottom="none";

		}else{
			const _legend = document.querySelector('li#legend_bottom');
			const _canon = document.querySelector('li#canon_bottom');

			_canon.style.borderBottom ="3px solid green";
			_legend.style.borderBottom="none";
		}

	}
	const canon = document.querySelector('li#canon_bottom');
	const legend = document.querySelector('li#legend_bottom');

	return(
		<div className="stories">
			<BrowserRouter >
				<DrawerStories className="topBar" whenClick={borderChanger}/>
				<Switch>
		            <Route exact path='/canon' component={Canon}/>
		            <Route  path='/legends' component={Legends}/>
	      		</Switch>
			</BrowserRouter>
		</div>
	);
}
