	}
	const canon = document.querySelector('li#canon_bottom');
	const legend = document.querySelector('li#legend_bottom');
	const whatBorder = true;

	function borderLegend(){

		if(whatBorder == true){
			legend.style.borderBottom = "10px solid green";
			canon.style.borderBottom ="none";
			whatBorder = false;
		}

	}
	function borderCanon(){

		if(whatBorder == false){
			canon.style.borderBottom = "10px solid green";
			legend.style.borderBottom ="none";
			whatBorder = true;
		}

	}
	return(
		<div className="stories">
			<BrowserRouter >
				<DrawerStories className="topBar" whenClick={borderChanger()}/>
				<Switch>
		            <Route exact path='/canon' component={Canon}/>
		            <Route  path='/legends' component={Legends}/>
	      		</Switch>
			</BrowserRouter>
		</div>
	);
}
